package path;import java.util.List;public class Main {    public static String addCountriesToCities(List<List<String>> paths, List<List<String>> cityWithCountry){        PathReader reader = new PathReader();        String pathWithCountry = "";        for (List<String> path:paths){            List<String> pathList = reader.addCountries(String.join("->",path),cityWithCountry);            pathWithCountry += String.join("->",pathList)+"\n";        }        return pathWithCountry;    }    public static void main(String[] args) {        try{            PathReader reader = new PathReader();            TravelAgent agent = new TravelAgent();            List<List<String>> cityWithCountry = reader.readCities( args[3]);            agent.data = reader.readData( args[1]);            List<List<String>> allPaths = agent.getPaths(args[4], args[5]);            System.out.println(addCountriesToCities(allPaths,cityWithCountry));        }catch(Exception e){            e.printStackTrace();        }    }}